using System.Text.RegularExpressions;

namespace adventofcode2022._2022
{
    internal static class _06
    {
        static int size = 14;
        static string args = "";

        public static void Run()
        {
            int counter = 0;
            string bufor = "";
            foreach (var item in args)
            {
                if (bufor.Length == size && bufor.Distinct().Count() == size)
                {
                    Console.WriteLine(counter);
                    break;
                }

                if (bufor.Length == size)
                    bufor = new string(bufor.Skip(1).ToArray());

                counter++;
                bufor += item;
            }
        }
    }
}
