    public static void Run()
    {
        var redLimit = 12;
        var greenLimit = 13;
        var blueLimit = 14;
        
        var split = args.Split("\n")
            .Select(row =>
            {
                var gameInfo = row.Split(":");
                var gameId = int.Parse(gameInfo[0].Split(" ")[1]);

                return new
                {
                    Id = gameId,
                    Value = gameInfo[1].Split(";").Select(game =>
                        {
                            var cubes = game.Split(",");
    
                            var blue = cubes.FirstOrDefault(c => c.Contains("blue"));
                            var red = cubes.FirstOrDefault(c => c.Contains("red"));
                            var green = cubes.FirstOrDefault(c => c.Contains("green"));
    
                            return new
                            {
                                Blue = blue == null
                                    ? 0
                                    : int.Parse(blue.Split(" ", StringSplitOptions.RemoveEmptyEntries)[0]),
                                Red = red == null ? 0 : int.Parse(red.Split(" ", StringSplitOptions.RemoveEmptyEntries)[0]),
                                Green = green == null
                                    ? 0
                                    : int.Parse(green.Split(" ", StringSplitOptions.RemoveEmptyEntries)[0])
                            };
                        }).ToArray()
                };
            }).ToArray();

        // var result = split
        //     .Where(g =>
        //         g.Value.All(c => c.Blue <= blueLimit && c.Red <= redLimit && c.Green <= greenLimit))
        //     .Sum(g => g.Id);

        var result = split
            .Select(x =>x.Value.Max(y => y.Blue) *
                         x.Value.Max(y => y.Red) *
                         x.Value.Max(y => y.Green))
            .Sum();
        
        Console.WriteLine(result);
    }
